<script>
  import { defineComponent, computed } from 'vue';

  const Sticker = defineComponent({
    name: 'QSticker',
    props: {
      color: String,
      size: String,
    },
    emits: ['click'],
    setup(props, ctx) {
      const iconStyle = computed(() => ({
        display: 'inline-block',
        fontSize: props.size,
        color: props.color,
        width: '1em',
        height: '1em',
        lineHeight: '1em',
      }));

      const click = (e) => ctx.emit('click', e);

      return { iconStyle, click };
    }
  });

  Sticker.install = (app) => {
    app.component(Sticker.name, Sticker);
  }

  export default Sticker;
</script>
